document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll('[data-ss-toggle="dialog"]').forEach(t=>{t.addEventListener("click",()=>{var e=document.querySelector(t.getAttribute("data-ss-target"));e&&e.classList.add("show")})}),document.querySelectorAll(".dialog-backdrop").forEach(t=>{t.addEventListener("click",()=>{var e=t.closest(".dialog");e&&e.classList.remove("show")})}),document.querySelectorAll('[data-ss-dismiss="dialog"]').forEach(t=>{t.addEventListener("click",()=>{var e=t.closest(".dialog");e&&e.classList.remove("show")})}),document.querySelectorAll('[data-ss-toggle="accordion"]').forEach(t=>{t.addEventListener("click",e=>{e.preventDefault();e=document.querySelector(t.getAttribute("data-ss-target"));e&&(e.classList.toggle("show"),t.classList.toggle("open"))})})});