document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll('[data-toggle="dialog"]').forEach(t=>{t.addEventListener("click",()=>{var e=document.querySelector(t.getAttribute("data-target"));e&&e.classList.add("show")})}),document.querySelectorAll(".dialog-backdrop").forEach(t=>{t.addEventListener("click",()=>{var e=t.closest(".dialog");e&&e.classList.remove("show")})}),document.querySelectorAll('[data-dismiss="dialog"]').forEach(t=>{t.addEventListener("click",()=>{var e=t.closest(".dialog");e&&e.classList.remove("show")})})});